export default {
  data: function data() {
    return {
      uname: "",
      upwd: "",
      unameholder: "请输入用户名",
      upwdholder: "请输入密码"
    };
  },
  methods: {
    login: function login() {
      var _this = this;

      // 功能：完成用户登录操作
      // 1获取用户输入用户名
      // 2获取用户输入密码
      // 3创建正则表达式验证用户名和密码3~12字母数字
      // 4用户验证失败 提示短消息
      // 5密码验证失败 提示短消息
      // 6发送ajax 请求axios ?
      // 7获取服务器返回结果
      // 7.1 登录失败 提示
      // 7.2登录成功 跳转商品首页组件    
      var reg = /^[a-z0-9]{3,12}$/i;
      var u = this.uname;
      var p = this.upwd;

      if (!reg.test(u)) {
        this.$toast("用户名格式不正确");
        return;
      }

      if (!reg.test(p)) {
        this.$toast("密码格式不正确");
        return;
      }

      var url = "login";
      var obj = {
        uname: u,
        upwd: p
      };
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        //7:获取服务器返回结果
        //7.1:登录失败 提示
        if (res.data.code == -1) {
          _this.$toast("用户名和密码有误");
        } else {
          //7.2：登录成功 跳转商品首页组件
          _this.$router.push("/product");
        }
      });
    }
  }
};
